#[test]
fn test_vec4_eq() {
    assert_eq!(
        vec4!(0i64, 1i64, 2i64, 3i64) == vec4!(0i64, 1i64, 2i64, 3i64),
        true
    );
    assert_eq!(
        vec4!(0i64, 1i64, 2i64, 3i64) == vec4!(0i64, 0i64, 2i64, 3i64),
        false
    );
    assert_eq!(
        vec4!(0i64, 1i64, 2i64, 3i64) == vec4!(4i64, 5i64, 6i64, 7i64),
        false
    );
}

#[test]
fn test_vec4_ne() {
    assert_eq!(
        vec4!(0i64, 1i64, 2i64, 3i64) != vec4!(0i64, 1i64, 2i64, 3i64),
        false
    );
    assert_eq!(
        vec4!(0i64, 1i64, 2i64, 3i64) != vec4!(0i64, 0i64, 2i64, 3i64),
        true
    );
    assert_eq!(
        vec4!(0i64, 1i64, 2i64, 3i64) != vec4!(4i64, 5i64, 6i64, 7i64),
        true
    );
}

