#[test]
fn test_simd_int_fns() {
    assert_eq!(!vec2!(5i64, 7i64), vec2!(!5i64, !7i64));
    assert_eq!(!vec3!(5i64, 7i64, 9i64), vec3!(!5i64, !7i64, !9i64));
    assert_eq!(
        !vec4!(5i64, 7i64, 9i64, 11i64),
        vec4!(!5i64, !7i64, !9i64, !11i64)
    );

    assert_eq!(
        vec2!(5i64, 7i64) + vec2!(9i64, 11i64),
        vec2!(5i64 + 9i64, 7i64 + 11i64)
    );
    assert_eq!(
        vec3!(5i64, 7i64, 9i64) + vec3!(11i64, 13i64, 15i64),
        vec3!(5i64 + 11i64, 7i64 + 13i64, 9i64 + 15i64)
    );
    assert_eq!(
        vec4!(5i64, 7i64, 9i64, 11i64) + vec4!(13i64, 15i64, 17i64, 19i64),
        vec4!(5i64 + 13i64, 7i64 + 15i64, 9i64 + 17i64, 11i64 + 19i64)
    );

    assert_eq!(
        vec2!(9i64, 11i64) - vec2!(5i64, 7i64),
        vec2!(9i64 - 5i64, 11i64 - 7i64)
    );
    assert_eq!(
        vec3!(9i64, 11i64, 13i64) - vec3!(5i64, 7i64, 9i64),
        vec3!(9i64 - 5i64, 11i64 - 7i64, 13i64 - 9i64)
    );
    assert_eq!(
        vec4!(9i64, 11i64, 13i64, 15i64) - vec4!(5i64, 7i64, 9i64, 11i64),
        vec4!(9i64 - 5i64, 11i64 - 7i64, 13i64 - 9i64, 15i64 - 11i64)
    );

    assert_eq!(
        vec2!(2i64, 4i64) * vec2!(3i64, 5i64),
        vec2!(2i64 * 3i64, 4i64 * 5i64)
    );
    assert_eq!(
        vec3!(2i64, 4i64, 6i64) * vec3!(3i64, 5i64, 7i64),
        vec3!(2i64 * 3i64, 4i64 * 5i64, 6i64 * 7i64)
    );
    assert_eq!(
        vec4!(2i64, 4i64, 6i64, 8i64) * vec4!(3i64, 5i64, 7i64, 9i64),
        vec4!(2i64 * 3i64, 4i64 * 5i64, 6i64 * 7i64, 8i64 * 9i64)
    );

    assert_eq!(
        vec2!(21i64, 23i64) / vec2!(4i64, 50i64),
        vec2!(21i64 / 4i64, 23i64 / 50i64)
    );
    assert_eq!(
        vec3!(21i64, 23i64, 25i64) / vec3!(4i64, 50i64, 3i64),
        vec3!(21i64 / 4i64, 23i64 / 50i64, 25i64 / 3i64)
    );
    assert_eq!(
        vec4!(21i64, 23i64, 25i64, 27i64) / vec4!(4i64, 50i64, 3i64, 10i64),
        vec4!(21i64 / 4i64, 23i64 / 50i64, 25i64 / 3i64, 27i64 / 10i64)
    );

    assert_eq!(
        vec2!(16i64, 19i64) / vec2!(5i64, 5i64),
        vec2!(16i64 / 5i64, 19i64 / 5i64)
    );
    assert_eq!(
        vec3!(16i64, 19i64, 22i64) / vec3!(5i64, 5i64, 5i64),
        vec3!(16i64 / 5i64, 19i64 / 5i64, 22i64 / 5i64)
    );
    assert_eq!(
        vec4!(16i64, 19i64, 22i64, 25i64) / vec4!(5i64, 5i64, 5i64, 5i64),
        vec4!(16i64 / 5i64, 19i64 / 5i64, 22i64 / 5i64, 25i64 / 5i64)
    );

    assert_eq!(
        vec2!(21i64, 23i64) % vec2!(4i64, 50i64),
        vec2!(21i64 % 4i64, 23i64 % 50i64)
    );
    assert_eq!(
        vec3!(21i64, 23i64, 25i64) % vec3!(4i64, 50i64, 3i64),
        vec3!(21i64 % 4i64, 23i64 % 50i64, 25i64 % 3i64)
    );
    assert_eq!(
        vec4!(21i64, 23i64, 25i64, 27i64) % vec4!(4i64, 50i64, 3i64, 10i64),
        vec4!(21i64 % 4i64, 23i64 % 50i64, 25i64 % 3i64, 27i64 % 10i64)
    );

    assert_eq!(
        vec2!(21i64, 23i64) & vec2!(4i64, 50i64),
        vec2!(21i64 & 4i64, 23i64 & 50i64)
    );
    assert_eq!(
        vec3!(21i64, 23i64, 25i64) & vec3!(4i64, 50i64, 3i64),
        vec3!(21i64 & 4i64, 23i64 & 50i64, 25i64 & 3i64)
    );
    assert_eq!(
        vec4!(21i64, 23i64, 25i64, 27i64) & vec4!(4i64, 50i64, 3i64, 10i64),
        vec4!(21i64 & 4i64, 23i64 & 50i64, 25i64 & 3i64, 27i64 & 10i64)
    );

    assert_eq!(
        vec2!(21i64, 23i64) | vec2!(4i64, 50i64),
        vec2!(21i64 | 4i64, 23i64 | 50i64)
    );
    assert_eq!(
        vec3!(21i64, 23i64, 25i64) | vec3!(4i64, 50i64, 3i64),
        vec3!(21i64 | 4i64, 23i64 | 50i64, 25i64 | 3i64)
    );
    assert_eq!(
        vec4!(21i64, 23i64, 25i64, 27i64) | vec4!(4i64, 50i64, 3i64, 10i64),
        vec4!(21i64 | 4i64, 23i64 | 50i64, 25i64 | 3i64, 27i64 | 10i64)
    );

    assert_eq!(
        vec2!(21i64, 23i64) ^ vec2!(4i64, 50i64),
        vec2!(21i64 ^ 4i64, 23i64 ^ 50i64)
    );
    assert_eq!(
        vec3!(21i64, 23i64, 25i64) ^ vec3!(4i64, 50i64, 3i64),
        vec3!(21i64 ^ 4i64, 23i64 ^ 50i64, 25i64 ^ 3i64)
    );
    assert_eq!(
        vec4!(21i64, 23i64, 25i64, 27i64) ^ vec4!(4i64, 50i64, 3i64, 10i64),
        vec4!(21i64 ^ 4i64, 23i64 ^ 50i64, 25i64 ^ 3i64, 27i64 ^ 10i64)
    );

    assert_eq!(Vec2::ZERO, vec2!(0i64, 0i64));
    assert_eq!(Vec3::ZERO, vec3!(0i64, 0i64, 0i64));
    assert_eq!(Vec4::ZERO, vec4!(0i64, 0i64, 0i64, 0i64));

    assert_eq!(Vec2::ONE, vec2!(1i64, 1i64));
    assert_eq!(Vec3::ONE, vec3!(1i64, 1i64, 1i64));
    assert_eq!(Vec4::ONE, vec4!(1i64, 1i64, 1i64, 1i64));

    assert_eq!(Vec2::X, vec2!(1i64, 0i64));
    assert_eq!(Vec3::Y, vec3!(0i64, 1i64, 0i64));
    assert_eq!(Vec4::Z, vec4!(0i64, 0i64, 1i64, 0i64));

    #[cfg(feature = "right")]
    {
        use ggmath::right::*;

        assert_eq!(Vec2::<i64>::RIGHT, Vec2::<i64>::X);
        assert_eq!(Vec3::<i64>::RIGHT, Vec3::<i64>::X);
        assert_eq!(Vec4::<i64>::RIGHT, Vec4::<i64>::X);
    }

    #[cfg(feature = "left")]
    {
        use ggmath::left::*;

        assert_eq!(Vec2::<i64>::LEFT, Vec2::<i64>::X);
        assert_eq!(Vec3::<i64>::LEFT, Vec3::<i64>::X);
        assert_eq!(Vec4::<i64>::LEFT, Vec4::<i64>::X);
    }

    #[cfg(feature = "backwards")]
    {
        use ggmath::backwards::*;

        assert_eq!(Vec3::<i64>::BACKWARDS, Vec3::<i64>::Z);
        assert_eq!(Vec4::<i64>::BACKWARDS, Vec4::<i64>::Z);
    }
}

macro_rules! test_simd_binop_edgecase {
    ( $(#[$attr:meta])* $vec2_fn:ident, $vec3_fn:ident, $vec4_fn:ident: $op:tt for $lhs:expr, $rhs:expr) => {
        #[test]
        $(#[$attr])*
        fn $vec2_fn() {
            assert_eq!(vec2!(1i64, $lhs) $op vec2!(1i64, $rhs), vec2!(1i64 $op 1i64, $lhs $op $rhs));
        }

        #[test]
        $(#[$attr])*
        fn $vec3_fn() {
            assert_eq!(vec3!(1i64, $lhs, 1i64) $op vec3!(1i64, $rhs, 1i64), vec3!(1i64 $op 1i64, $lhs $op $rhs, 1i64 $op 1i64));
        }

        #[test]
        $(#[$attr])*
        fn $vec4_fn() {
            assert_eq!(vec4!(1i64, $lhs, 1i64, 1i64) $op vec4!(1i64, $rhs, 1i64, 1i64), vec4!(1i64 $op 1i64, $lhs $op $rhs, 1i64 $op 1i64, 1i64 $op 1i64));
        }
    }
}

