fn test_i8vec4s_eq_mask() {
    assert_eq!(
        vec4s!(0i8, 1i8, 2i8, 3i8).eq_mask(vec4s!(0i8, 1i8, 2i8, 3i8)),
        vec4s!(true, true, true, true)
    );
    assert_eq!(
        vec4s!(0i8, 1i8, 2i8, 3i8).eq_mask(vec4s!(0i8, 0i8, 2i8, 3i8)),
        vec4s!(true, false, true, true),
    );
    assert_eq!(
        vec4s!(0i8, 1i8, 2i8, 3i8).eq_mask(vec4s!(4i8, 5i8, 6i8, 7i8)),
        vec4s!(false, false, false, false)
    );
}

#[test]
fn test_i8vec4s_ne_mask() {
    assert_eq!(
        vec4s!(0i8, 1i8, 2i8, 3i8).ne_mask(vec4s!(0i8, 1i8, 2i8, 3i8)),
        vec4s!(false, false, false, false)
    );
    assert_eq!(
        vec4s!(0i8, 1i8, 2i8, 3i8).ne_mask(vec4s!(0i8, 0i8, 2i8, 3i8)),
        vec4s!(false, true, false, false),
    );
    assert_eq!(
        vec4s!(0i8, 1i8, 2i8, 3i8).ne_mask(vec4s!(4i8, 5i8, 6i8, 7i8)),
        vec4s!(true, true, true, true)
    );
}

