#[test]
fn test_i8vec4s_eq() {
    assert_eq!(
        vec4s!(0i8, 1i8, 2i8, 3i8) == vec4s!(0i8, 1i8, 2i8, 3i8),
        true
    );
    assert_eq!(
        vec4s!(0i8, 1i8, 2i8, 3i8) == vec4s!(0i8, 0i8, 2i8, 3i8),
        false
    );
    assert_eq!(
        vec4s!(0i8, 1i8, 2i8, 3i8) == vec4s!(4i8, 5i8, 6i8, 7i8),
        false
    );
}

#[test]
fn test_i8vec4s_ne() {
    assert_eq!(
        vec4s!(0i8, 1i8, 2i8, 3i8) != vec4s!(0i8, 1i8, 2i8, 3i8),
        false
    );
    assert_eq!(
        vec4s!(0i8, 1i8, 2i8, 3i8) != vec4s!(0i8, 0i8, 2i8, 3i8),
        true
    );
    assert_eq!(
        vec4s!(0i8, 1i8, 2i8, 3i8) != vec4s!(4i8, 5i8, 6i8, 7i8),
        true
    );
}

#[test]
fn test_i8vec4s_debug() {
    assert_eq!(format!("{:?}", vec4s!(0i8, 1i8, 2i8, 3i8)), "(0, 1, 2, 3)");
}

#[test]
fn test_i8vec4s_display() {
    assert_eq!(format!("{}", vec4s!(0i8, 1i8, 2i8, 3i8)), "(0, 1, 2, 3)");
}

#[test]
fn test_i8vec4s_const_from_array() {
    assert_eq!(
        Vec4S::<i8>::const_from_array([0i8, 1i8, 2i8, 3i8]),
        Vec4S::from_array([0i8, 1i8, 2i8, 3i8])
    );
}
