#[test]
fn test_vec4p_try_set() {
    {
        let mut vec = vec4p!(0i8, 1i8, 2i8, 3i8);
        vec.try_set(0, 50i8).unwrap();

        assert_eq!(vec, vec4p!(50i8, 1i8, 2i8, 3i8));
    }
    {
        let mut vec = vec4p!(0i8, 1i8, 2i8, 3i8);
        vec.try_set(1, 50i8).unwrap();

        assert_eq!(vec, vec4p!(0i8, 50i8, 2i8, 3i8));
    }
    {
        let mut vec = vec4p!(0i8, 1i8, 2i8, 3i8);
        vec.try_set(2, 50i8).unwrap();

        assert_eq!(vec, vec4p!(0i8, 1i8, 50i8, 3i8));
    }
    {
        let mut vec = vec4p!(0i8, 1i8, 2i8, 3i8);
        vec.try_set(3, 50i8).unwrap();

        assert_eq!(vec, vec4p!(0i8, 1i8, 2i8, 50i8));
    }

    assert_eq!(
        vec4p!(0i8, 1i8, 2i8, 3i8).try_set(4, 0i8),
        Err(IndexOutOfBoundsError)
    );
}

