#[test]
fn test_simd_int_fns() {
    assert_eq!(!vec2!(5u128, 7u128), vec2!(!5u128, !7u128));
    assert_eq!(!vec3!(5u128, 7u128, 9u128), vec3!(!5u128, !7u128, !9u128));
    assert_eq!(
        !vec4!(5u128, 7u128, 9u128, 11u128),
        vec4!(!5u128, !7u128, !9u128, !11u128)
    );

    assert_eq!(
        vec2!(5u128, 7u128) + vec2!(9u128, 11u128),
        vec2!(5u128 + 9u128, 7u128 + 11u128)
    );
    assert_eq!(
        vec3!(5u128, 7u128, 9u128) + vec3!(11u128, 13u128, 15u128),
        vec3!(5u128 + 11u128, 7u128 + 13u128, 9u128 + 15u128)
    );
    assert_eq!(
        vec4!(5u128, 7u128, 9u128, 11u128) + vec4!(13u128, 15u128, 17u128, 19u128),
        vec4!(
            5u128 + 13u128,
            7u128 + 15u128,
            9u128 + 17u128,
            11u128 + 19u128
        )
    );

    assert_eq!(
        vec2!(9u128, 11u128) - vec2!(5u128, 7u128),
        vec2!(9u128 - 5u128, 11u128 - 7u128)
    );
    assert_eq!(
        vec3!(9u128, 11u128, 13u128) - vec3!(5u128, 7u128, 9u128),
        vec3!(9u128 - 5u128, 11u128 - 7u128, 13u128 - 9u128)
    );
    assert_eq!(
        vec4!(9u128, 11u128, 13u128, 15u128) - vec4!(5u128, 7u128, 9u128, 11u128),
        vec4!(
            9u128 - 5u128,
            11u128 - 7u128,
            13u128 - 9u128,
            15u128 - 11u128
        )
    );

    assert_eq!(
        vec2!(2u128, 4u128) * vec2!(3u128, 5u128),
        vec2!(2u128 * 3u128, 4u128 * 5u128)
    );
    assert_eq!(
        vec3!(2u128, 4u128, 6u128) * vec3!(3u128, 5u128, 7u128),
        vec3!(2u128 * 3u128, 4u128 * 5u128, 6u128 * 7u128)
    );
    assert_eq!(
        vec4!(2u128, 4u128, 6u128, 8u128) * vec4!(3u128, 5u128, 7u128, 9u128),
        vec4!(2u128 * 3u128, 4u128 * 5u128, 6u128 * 7u128, 8u128 * 9u128)
    );

    assert_eq!(
        vec2!(21u128, 23u128) / vec2!(4u128, 50u128),
        vec2!(21u128 / 4u128, 23u128 / 50u128)
    );
    assert_eq!(
        vec3!(21u128, 23u128, 25u128) / vec3!(4u128, 50u128, 3u128),
        vec3!(21u128 / 4u128, 23u128 / 50u128, 25u128 / 3u128)
    );
    assert_eq!(
        vec4!(21u128, 23u128, 25u128, 27u128) / vec4!(4u128, 50u128, 3u128, 10u128),
        vec4!(
            21u128 / 4u128,
            23u128 / 50u128,
            25u128 / 3u128,
            27u128 / 10u128
        )
    );

    assert_eq!(
        vec2!(16u128, 19u128) / vec2!(5u128, 5u128),
        vec2!(16u128 / 5u128, 19u128 / 5u128)
    );
    assert_eq!(
        vec3!(16u128, 19u128, 22u128) / vec3!(5u128, 5u128, 5u128),
        vec3!(16u128 / 5u128, 19u128 / 5u128, 22u128 / 5u128)
    );
    assert_eq!(
        vec4!(16u128, 19u128, 22u128, 25u128) / vec4!(5u128, 5u128, 5u128, 5u128),
        vec4!(
            16u128 / 5u128,
            19u128 / 5u128,
            22u128 / 5u128,
            25u128 / 5u128
        )
    );

    assert_eq!(
        vec2!(21u128, 23u128) % vec2!(4u128, 50u128),
        vec2!(21u128 % 4u128, 23u128 % 50u128)
    );
    assert_eq!(
        vec3!(21u128, 23u128, 25u128) % vec3!(4u128, 50u128, 3u128),
        vec3!(21u128 % 4u128, 23u128 % 50u128, 25u128 % 3u128)
    );
    assert_eq!(
        vec4!(21u128, 23u128, 25u128, 27u128) % vec4!(4u128, 50u128, 3u128, 10u128),
        vec4!(
            21u128 % 4u128,
            23u128 % 50u128,
            25u128 % 3u128,
            27u128 % 10u128
        )
    );

    assert_eq!(
        vec2!(21u128, 23u128) & vec2!(4u128, 50u128),
        vec2!(21u128 & 4u128, 23u128 & 50u128)
    );
    assert_eq!(
        vec3!(21u128, 23u128, 25u128) & vec3!(4u128, 50u128, 3u128),
        vec3!(21u128 & 4u128, 23u128 & 50u128, 25u128 & 3u128)
    );
    assert_eq!(
        vec4!(21u128, 23u128, 25u128, 27u128) & vec4!(4u128, 50u128, 3u128, 10u128),
        vec4!(
            21u128 & 4u128,
            23u128 & 50u128,
            25u128 & 3u128,
            27u128 & 10u128
        )
    );

    assert_eq!(
        vec2!(21u128, 23u128) | vec2!(4u128, 50u128),
        vec2!(21u128 | 4u128, 23u128 | 50u128)
    );
    assert_eq!(
        vec3!(21u128, 23u128, 25u128) | vec3!(4u128, 50u128, 3u128),
        vec3!(21u128 | 4u128, 23u128 | 50u128, 25u128 | 3u128)
    );
    assert_eq!(
        vec4!(21u128, 23u128, 25u128, 27u128) | vec4!(4u128, 50u128, 3u128, 10u128),
        vec4!(
            21u128 | 4u128,
            23u128 | 50u128,
            25u128 | 3u128,
            27u128 | 10u128
        )
    );

    assert_eq!(
        vec2!(21u128, 23u128) ^ vec2!(4u128, 50u128),
        vec2!(21u128 ^ 4u128, 23u128 ^ 50u128)
    );
    assert_eq!(
        vec3!(21u128, 23u128, 25u128) ^ vec3!(4u128, 50u128, 3u128),
        vec3!(21u128 ^ 4u128, 23u128 ^ 50u128, 25u128 ^ 3u128)
    );
    assert_eq!(
        vec4!(21u128, 23u128, 25u128, 27u128) ^ vec4!(4u128, 50u128, 3u128, 10u128),
        vec4!(
            21u128 ^ 4u128,
            23u128 ^ 50u128,
            25u128 ^ 3u128,
            27u128 ^ 10u128
        )
    );

    assert_eq!(Vec2::ZERO, vec2!(0u128, 0u128));
    assert_eq!(Vec3::ZERO, vec3!(0u128, 0u128, 0u128));
    assert_eq!(Vec4::ZERO, vec4!(0u128, 0u128, 0u128, 0u128));

    assert_eq!(Vec2::ONE, vec2!(1u128, 1u128));
    assert_eq!(Vec3::ONE, vec3!(1u128, 1u128, 1u128));
    assert_eq!(Vec4::ONE, vec4!(1u128, 1u128, 1u128, 1u128));

    assert_eq!(Vec2::X, vec2!(1u128, 0u128));
    assert_eq!(Vec3::Y, vec3!(0u128, 1u128, 0u128));
    assert_eq!(Vec4::Z, vec4!(0u128, 0u128, 1u128, 0u128));

    #[cfg(feature = "right")]
    {
        use ggmath::right::*;

        assert_eq!(Vec2::<u128>::RIGHT, Vec2::<u128>::X);
        assert_eq!(Vec3::<u128>::RIGHT, Vec3::<u128>::X);
        assert_eq!(Vec4::<u128>::RIGHT, Vec4::<u128>::X);
    }

    #[cfg(feature = "left")]
    {
        use ggmath::left::*;

        assert_eq!(Vec2::<u128>::LEFT, Vec2::<u128>::X);
        assert_eq!(Vec3::<u128>::LEFT, Vec3::<u128>::X);
        assert_eq!(Vec4::<u128>::LEFT, Vec4::<u128>::X);
    }

    #[cfg(feature = "backwards")]
    {
        use ggmath::backwards::*;

        assert_eq!(Vec3::<u128>::BACKWARDS, Vec3::<u128>::Z);
        assert_eq!(Vec4::<u128>::BACKWARDS, Vec4::<u128>::Z);
    }
}

macro_rules! test_simd_binop_edgecase {
    ( $(#[$attr:meta])* $vec2_fn:ident, $vec3_fn:ident, $vec4_fn:ident: $op:tt for $lhs:expr, $rhs:expr) => {
        #[test]
        $(#[$attr])*
        fn $vec2_fn() {
            assert_eq!(vec2!(1u128, $lhs) $op vec2!(1u128, $rhs), vec2!(1u128 $op 1u128, $lhs $op $rhs));
        }

        #[test]
        $(#[$attr])*
        fn $vec3_fn() {
            assert_eq!(vec3!(1u128, $lhs, 1u128) $op vec3!(1u128, $rhs, 1u128), vec3!(1u128 $op 1u128, $lhs $op $rhs, 1u128 $op 1u128));
        }

        #[test]
        $(#[$attr])*
        fn $vec4_fn() {
            assert_eq!(vec4!(1u128, $lhs, 1u128, 1u128) $op vec4!(1u128, $rhs, 1u128, 1u128), vec4!(1u128 $op 1u128, $lhs $op $rhs, 1u128 $op 1u128, 1u128 $op 1u128));
        }
    }
}

