#[test]
fn test_nonsimd_int_fns() {
    assert_eq!(!vec2s!(5u128, 7u128), vec2s!(!5u128, !7u128));
    assert_eq!(!vec3s!(5u128, 7u128, 9u128), vec3s!(!5u128, !7u128, !9u128));
    assert_eq!(
        !vec4s!(5u128, 7u128, 9u128, 11u128),
        vec4s!(!5u128, !7u128, !9u128, !11u128)
    );

    assert_eq!(
        vec2s!(5u128, 7u128) + vec2s!(9u128, 11u128),
        vec2s!(5u128 + 9u128, 7u128 + 11u128)
    );
    assert_eq!(
        vec3s!(5u128, 7u128, 9u128) + vec3s!(11u128, 13u128, 15u128),
        vec3s!(5u128 + 11u128, 7u128 + 13u128, 9u128 + 15u128)
    );
    assert_eq!(
        vec4s!(5u128, 7u128, 9u128, 11u128) + vec4s!(13u128, 15u128, 17u128, 19u128),
        vec4s!(
            5u128 + 13u128,
            7u128 + 15u128,
            9u128 + 17u128,
            11u128 + 19u128
        )
    );

    assert_eq!(
        vec2s!(9u128, 11u128) - vec2s!(5u128, 7u128),
        vec2s!(9u128 - 5u128, 11u128 - 7u128)
    );
    assert_eq!(
        vec3s!(9u128, 11u128, 13u128) - vec3s!(5u128, 7u128, 9u128),
        vec3s!(9u128 - 5u128, 11u128 - 7u128, 13u128 - 9u128)
    );
    assert_eq!(
        vec4s!(9u128, 11u128, 13u128, 15u128) - vec4s!(5u128, 7u128, 9u128, 11u128),
        vec4s!(
            9u128 - 5u128,
            11u128 - 7u128,
            13u128 - 9u128,
            15u128 - 11u128
        )
    );

    assert_eq!(
        vec2s!(2u128, 4u128) * vec2s!(3u128, 5u128),
        vec2s!(2u128 * 3u128, 4u128 * 5u128)
    );
    assert_eq!(
        vec3s!(2u128, 4u128, 6u128) * vec3s!(3u128, 5u128, 7u128),
        vec3s!(2u128 * 3u128, 4u128 * 5u128, 6u128 * 7u128)
    );
    assert_eq!(
        vec4s!(2u128, 4u128, 6u128, 8u128) * vec4s!(3u128, 5u128, 7u128, 9u128),
        vec4s!(2u128 * 3u128, 4u128 * 5u128, 6u128 * 7u128, 8u128 * 9u128)
    );

    assert_eq!(
        vec2s!(21u128, 23u128) / vec2s!(4u128, 50u128),
        vec2s!(21u128 / 4u128, 23u128 / 50u128)
    );
    assert_eq!(
        vec3s!(21u128, 23u128, 25u128) / vec3s!(4u128, 50u128, 3u128),
        vec3s!(21u128 / 4u128, 23u128 / 50u128, 25u128 / 3u128)
    );
    assert_eq!(
        vec4s!(21u128, 23u128, 25u128, 27u128) / vec4s!(4u128, 50u128, 3u128, 10u128),
        vec4s!(
            21u128 / 4u128,
            23u128 / 50u128,
            25u128 / 3u128,
            27u128 / 10u128
        )
    );

    assert_eq!(
        vec2s!(16u128, 19u128) / vec2s!(5u128, 5u128),
        vec2s!(16u128 / 5u128, 19u128 / 5u128)
    );
    assert_eq!(
        vec3s!(16u128, 19u128, 22u128) / vec3s!(5u128, 5u128, 5u128),
        vec3s!(16u128 / 5u128, 19u128 / 5u128, 22u128 / 5u128)
    );
    assert_eq!(
        vec4s!(16u128, 19u128, 22u128, 25u128) / vec4s!(5u128, 5u128, 5u128, 5u128),
        vec4s!(
            16u128 / 5u128,
            19u128 / 5u128,
            22u128 / 5u128,
            25u128 / 5u128
        )
    );

    assert_eq!(
        vec2s!(21u128, 23u128) % vec2s!(4u128, 50u128),
        vec2s!(21u128 % 4u128, 23u128 % 50u128)
    );
    assert_eq!(
        vec3s!(21u128, 23u128, 25u128) % vec3s!(4u128, 50u128, 3u128),
        vec3s!(21u128 % 4u128, 23u128 % 50u128, 25u128 % 3u128)
    );
    assert_eq!(
        vec4s!(21u128, 23u128, 25u128, 27u128) % vec4s!(4u128, 50u128, 3u128, 10u128),
        vec4s!(
            21u128 % 4u128,
            23u128 % 50u128,
            25u128 % 3u128,
            27u128 % 10u128
        )
    );

    assert_eq!(
        vec2s!(21u128, 23u128) & vec2s!(4u128, 50u128),
        vec2s!(21u128 & 4u128, 23u128 & 50u128)
    );
    assert_eq!(
        vec3s!(21u128, 23u128, 25u128) & vec3s!(4u128, 50u128, 3u128),
        vec3s!(21u128 & 4u128, 23u128 & 50u128, 25u128 & 3u128)
    );
    assert_eq!(
        vec4s!(21u128, 23u128, 25u128, 27u128) & vec4s!(4u128, 50u128, 3u128, 10u128),
        vec4s!(
            21u128 & 4u128,
            23u128 & 50u128,
            25u128 & 3u128,
            27u128 & 10u128
        )
    );

    assert_eq!(
        vec2s!(21u128, 23u128) | vec2s!(4u128, 50u128),
        vec2s!(21u128 | 4u128, 23u128 | 50u128)
    );
    assert_eq!(
        vec3s!(21u128, 23u128, 25u128) | vec3s!(4u128, 50u128, 3u128),
        vec3s!(21u128 | 4u128, 23u128 | 50u128, 25u128 | 3u128)
    );
    assert_eq!(
        vec4s!(21u128, 23u128, 25u128, 27u128) | vec4s!(4u128, 50u128, 3u128, 10u128),
        vec4s!(
            21u128 | 4u128,
            23u128 | 50u128,
            25u128 | 3u128,
            27u128 | 10u128
        )
    );

    assert_eq!(
        vec2s!(21u128, 23u128) ^ vec2s!(4u128, 50u128),
        vec2s!(21u128 ^ 4u128, 23u128 ^ 50u128)
    );
    assert_eq!(
        vec3s!(21u128, 23u128, 25u128) ^ vec3s!(4u128, 50u128, 3u128),
        vec3s!(21u128 ^ 4u128, 23u128 ^ 50u128, 25u128 ^ 3u128)
    );
    assert_eq!(
        vec4s!(21u128, 23u128, 25u128, 27u128) ^ vec4s!(4u128, 50u128, 3u128, 10u128),
        vec4s!(
            21u128 ^ 4u128,
            23u128 ^ 50u128,
            25u128 ^ 3u128,
            27u128 ^ 10u128
        )
    );

    assert_eq!(Vec2S::ZERO, vec2s!(0u128, 0u128));
    assert_eq!(Vec3S::ZERO, vec3s!(0u128, 0u128, 0u128));
    assert_eq!(Vec4S::ZERO, vec4s!(0u128, 0u128, 0u128, 0u128));

    assert_eq!(Vec2S::ONE, vec2s!(1u128, 1u128));
    assert_eq!(Vec3S::ONE, vec3s!(1u128, 1u128, 1u128));
    assert_eq!(Vec4S::ONE, vec4s!(1u128, 1u128, 1u128, 1u128));

    assert_eq!(Vec2S::X, vec2s!(1u128, 0u128));
    assert_eq!(Vec3S::Y, vec3s!(0u128, 1u128, 0u128));
    assert_eq!(Vec4S::Z, vec4s!(0u128, 0u128, 1u128, 0u128));

    #[cfg(feature = "right")]
    {
        use ggmath::right::*;

        assert_eq!(Vec2S::<u128>::RIGHT, Vec2S::<u128>::X);
        assert_eq!(Vec3S::<u128>::RIGHT, Vec3S::<u128>::X);
        assert_eq!(Vec4S::<u128>::RIGHT, Vec4S::<u128>::X);
    }

    #[cfg(feature = "left")]
    {
        use ggmath::left::*;

        assert_eq!(Vec2S::<u128>::LEFT, Vec2S::<u128>::X);
        assert_eq!(Vec3S::<u128>::LEFT, Vec3S::<u128>::X);
        assert_eq!(Vec4S::<u128>::LEFT, Vec4S::<u128>::X);
    }

    #[cfg(feature = "backwards")]
    {
        use ggmath::backwards::*;

        assert_eq!(Vec3S::<u128>::BACKWARDS, Vec3S::<u128>::Z);
        assert_eq!(Vec4S::<u128>::BACKWARDS, Vec4S::<u128>::Z);
    }
}

macro_rules! test_nonsimd_binop_edgecase {
    ( $(#[$attr:meta])* $vec2_fn:ident, $vec3_fn:ident, $vec4_fn:ident: $op:tt for $lhs:expr, $rhs:expr) => {
        #[test]
        $(#[$attr])*
        fn $vec2_fn() {
            assert_eq!(vec2s!(1u128, $lhs) $op vec2s!(1u128, $rhs), vec2s!(1u128 $op 1u128, $lhs $op $rhs));
        }

        #[test]
        $(#[$attr])*
        fn $vec3_fn() {
            assert_eq!(vec3s!(1u128, $lhs, 1u128) $op vec3s!(1u128, $rhs, 1u128), vec3s!(1u128 $op 1u128, $lhs $op $rhs, 1u128 $op 1u128));
        }

        #[test]
        $(#[$attr])*
        fn $vec4_fn() {
            assert_eq!(vec4s!(1u128, $lhs, 1u128, 1u128) $op vec4s!(1u128, $rhs, 1u128, 1u128), vec4s!(1u128 $op 1u128, $lhs $op $rhs, 1u128 $op 1u128, 1u128 $op 1u128));
        }
    }
}

test_nonsimd_binop_edgecase! {
    #[cfg_attr(debug_assertions, should_panic)]
    test_u128vec2s_add_overflow,
    test_u128vec3s_add_overflow,
    test_u128vec4s_add_overflow:

    + for u128::MAX, 3
}
