fn test_u8vec4s_eq_mask() {
    assert_eq!(
        vec4s!(0u8, 1u8, 2u8, 3u8).eq_mask(vec4s!(0u8, 1u8, 2u8, 3u8)),
        vec4s!(true, true, true, true)
    );
    assert_eq!(
        vec4s!(0u8, 1u8, 2u8, 3u8).eq_mask(vec4s!(0u8, 0u8, 2u8, 3u8)),
        vec4s!(true, false, true, true),
    );
    assert_eq!(
        vec4s!(0u8, 1u8, 2u8, 3u8).eq_mask(vec4s!(4u8, 5u8, 6u8, 7u8)),
        vec4s!(false, false, false, false)
    );
}

#[test]
fn test_u8vec4s_ne_mask() {
    assert_eq!(
        vec4s!(0u8, 1u8, 2u8, 3u8).ne_mask(vec4s!(0u8, 1u8, 2u8, 3u8)),
        vec4s!(false, false, false, false)
    );
    assert_eq!(
        vec4s!(0u8, 1u8, 2u8, 3u8).ne_mask(vec4s!(0u8, 0u8, 2u8, 3u8)),
        vec4s!(false, true, false, false),
    );
    assert_eq!(
        vec4s!(0u8, 1u8, 2u8, 3u8).ne_mask(vec4s!(4u8, 5u8, 6u8, 7u8)),
        vec4s!(true, true, true, true)
    );
}

