#[test]
fn test_u8vec4s_eq() {
    assert_eq!(
        vec4s!(0u8, 1u8, 2u8, 3u8) == vec4s!(0u8, 1u8, 2u8, 3u8),
        true
    );
    assert_eq!(
        vec4s!(0u8, 1u8, 2u8, 3u8) == vec4s!(0u8, 0u8, 2u8, 3u8),
        false
    );
    assert_eq!(
        vec4s!(0u8, 1u8, 2u8, 3u8) == vec4s!(4u8, 5u8, 6u8, 7u8),
        false
    );
}

#[test]
fn test_u8vec4s_ne() {
    assert_eq!(
        vec4s!(0u8, 1u8, 2u8, 3u8) != vec4s!(0u8, 1u8, 2u8, 3u8),
        false
    );
    assert_eq!(
        vec4s!(0u8, 1u8, 2u8, 3u8) != vec4s!(0u8, 0u8, 2u8, 3u8),
        true
    );
    assert_eq!(
        vec4s!(0u8, 1u8, 2u8, 3u8) != vec4s!(4u8, 5u8, 6u8, 7u8),
        true
    );
}

#[test]
fn test_u8vec4s_debug() {
    assert_eq!(format!("{:?}", vec4s!(0u8, 1u8, 2u8, 3u8)), "(0, 1, 2, 3)");
}

#[test]
fn test_u8vec4s_display() {
    assert_eq!(format!("{}", vec4s!(0u8, 1u8, 2u8, 3u8)), "(0, 1, 2, 3)");
}

#[test]
fn test_u8vec4s_const_from_array() {
    assert_eq!(
        Vec4S::<u8>::const_from_array([0u8, 1u8, 2u8, 3u8]),
        Vec4S::from_array([0u8, 1u8, 2u8, 3u8])
    );
}
